INSERT INTO `wp_wc_admin_notes` VALUES (29, 'woocommerce-core-paypal-march-2022-updated', 'update', 'en_US', 'Security auto-update of WooCommerce', '<strong>Your store has been updated to the latest secure version of WooCommerce</strong>. We worked with WordPress to deploy PayPal Standard security updates for stores running WooCommerce (version 3.5 to 6.3). It’s recommended to disable PayPal Standard, and use <a href="https://woocommerce.com/products/woocommerce-paypal-payments/" target="_blank">PayPal Payments</a> to accept PayPal.', '{}', 'pending', 'woocommerce.com', '2022-05-17 06:46:58', '', 0, 'plain', '', 0, 0, 'info'); 
INSERT INTO `wp_wc_admin_notes` VALUES (30, 'wc-refund-returns-page', 'info', 'en_US', 'Setup a Refund and Returns Policy page to boost your store\'s credibility.', 'We have created a sample draft Refund and Returns Policy page for you. Please have a look and update it to fit your store.', '{}', 'unactioned', 'woocommerce-core', '2022-05-17 06:46:58', '', 0, 'plain', '', 0, 0, 'info'); 
