INSERT INTO `wp_wc_admin_notes` VALUES (34, 'affirm_q2_2022', 'marketing', 'en_US', 'Boost your business with flexible payments', 'Expand your customer base with smarter payment options for more shoppers. With Affirm,  you can offer the most relevant payment options at every price point – from four interest-free payments every two weeks to longer installments up to 36 months. Fast-track your revenue goals today!', '{}', 'unactioned', 'woocommerce.com', '2022-05-17 06:46:58', '', 0, 'plain', '', 0, 0, 'info'); 
