INSERT INTO `wp_wc_admin_notes` VALUES (37, 'wc-admin-EU-consumer-protection', 'marketing', 'en_US', 'Important changes to EU consumer protection laws', 'New regulations to help modernize and strengthen consumer protection laws in the European Union (EU) take effect on May 28, 2022. These rules impact all merchants selling to the EU, regardless of where their business is located. Further detailed information is available on the European Commission\'s official website.', '{}', 'unactioned', 'woocommerce.com', '2022-05-17 06:46:58', '', 0, 'plain', '', 0, 0, 'info'); 
INSERT INTO `wp_wc_admin_notes` VALUES (38, 'googlelistings_multicountrygtm_q22022', 'marketing', 'en_US', 'Expand your audience with Multi-Country Advertising', 'It\'s time to expand your audience with Multi-Country Advertising, an exciting new feature in Google Listings &amp; Ads. Reach more potential customers in as many regions as you like within a single campaign, all from the convenience of your WooCommerce dashboard.', '{}', 'unactioned', 'woocommerce.com', '2022-05-17 06:46:58', '', 0, 'plain', '', 0, 0, 'info'); 
