INSERT INTO `wp_wc_admin_notes` VALUES (44, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2022-05-21 05:29:41', '', 0, 'plain', '', 0, 0, 'info'); 
INSERT INTO `wp_wc_admin_notes` VALUES (45, 'wc-admin-first-downloadable-product', 'info', 'en_US', 'Learn more about digital/downloadable products', 'Congrats on adding your first digital product! You can learn more about how to handle digital or downloadable products in our documentation.', '{}', 'unactioned', 'woocommerce-admin', '2022-05-21 05:29:41', '', 0, 'plain', '', 0, 0, 'info'); 
INSERT INTO `wp_wc_admin_notes` VALUES (46, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Start accepting payments on your store!', 'Take payments with the provider that’s right for you - choose from 100+ payment gateways for WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2022-05-22 15:05:56', '', 0, 'plain', '', 0, 0, 'info'); 
INSERT INTO `wp_wc_admin_notes` VALUES (47, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Help WooCommerce improve with usage tracking', 'Gathering usage data allows us to improve WooCommerce. Your store will be considered as we evaluate new features, judge the quality of an update, or determine if an improvement makes sense. You can always visit the <a href="http://localhost/ys_organic/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com" target="_blank">Settings</a> and choose to stop sharing data. <a href="https://woocommerce.com/usage-tracking?utm_medium=product" target="_blank">Read more</a> about what data we collect.', '{}', 'unactioned', 'woocommerce-admin', '2022-05-24 06:47:38', '', 0, 'plain', '', 0, 0, 'info'); 
INSERT INTO `wp_wc_admin_notes` VALUES (48, 'wc-admin-woocommerce-payments', 'marketing', 'en_US', 'Try the new way to get paid', 'Securely accept credit and debit cards on your site. Manage transactions without leaving your WordPress dashboard. Only with <strong>WooCommerce Payments</strong>.<br><br>By clicking "Get started", you agree to our <a href="https://wordpress.com/tos/" target="_blank">Terms of Service</a>', '{}', 'unactioned', 'woocommerce-admin', '2022-05-24 06:47:38', '', 0, 'plain', '', 0, 0, 'info'); 
INSERT INTO `wp_wc_admin_notes` VALUES (49, 'wc-admin-insight-first-sale', 'survey', 'en_US', 'Did you know?', 'A WooCommerce powered store needs on average 31 days to get the first sale. You\'re on the right track! Do you find this type of insight useful?', '{}', 'unactioned', 'woocommerce-admin', '2022-05-24 06:47:38', '', 0, 'plain', '', 0, 0, 'info'); 
INSERT INTO `wp_wc_admin_notes` VALUES (50, 'wc_ipp_order_creation_GTM_launch_q2_2022', 'marketing', 'en_US', 'Grow your business on the go with WooCommerce In-Person Payments', 'Quickly create new orders, manage transactions, and take secure payments no matter where your business takes you. With automatic inventory sync, WooCommerce In-Person Payments is the only fully integrated solution for taking your WooCommerce store offline.', '{}', 'unactioned', 'woocommerce.com', '2022-05-26 05:10:16', '', 0, 'plain', '', 0, 0, 'info'); 
INSERT INTO `wp_wc_admin_notes` VALUES (51, 'wc-admin-wisepad3', 'marketing', 'en_US', 'Take your business on the go in Canada with WooCommerce In-Person Payments', 'Quickly create new orders, accept payment in person for orders placed online, and automatically sync your inventory – no matter where your business takes you. With WooCommerce In-Person Payments and the WisePad 3 card reader, you can bring the power of your store anywhere.', '{}', 'pending', 'woocommerce.com', '2022-06-18 05:38:32', '', 0, 'plain', '', 0, 0, 'info'); 
INSERT INTO `wp_wc_admin_notes` VALUES (52, 'TikTok q2_2022', 'marketing', 'en_US', 'Give your store a stage on the world’s fastest-growing advertising channel', 'With TikTok for WooCommerce, you can sync your catalog, create videos, and track performance in front of TikTok’s one billion global users. Try the Smart Video Generator to make ads using your existing product images – no camera needed. Get $200 in ad credit from TikTok after a $20 spend (terms &amp; conditions apply).', '{}', 'unactioned', 'woocommerce.com', '2022-06-22 11:21:42', '', 0, 'plain', '', 0, 0, 'info');
